mapper.select:
select

        a.gpsid,

        a.uuid,

        a.simnohash,

        a.simno,

        a.vehicleid,

        a.createdate,

        a.sendtime,

        a.alarmstate,

        a.altitude,

        a.direction,

        a.gas,

        a.latitude,

        a.longitude,

        a.mileage,

        a.recordvelocity,

        a.status,

        a.valid,

        a.velocity,

        a.signalstate,

        a.abnormaltype,

        a.fromflag,

        a.extendversion,

        a.extendjson
from
gps_hisdata.gpsinfo_20200701 as a

where 1=1

            and a.simnohash=#{simnohash}


            and a.simno=#{simno}



            and a.vehicleid=#{vehicleid}


            and a.createdate >= #{startcreatedate }


            and  a.createdate < #{endcreatedate }


            and a.sendtime >= #{startsendtime }


            and  a.sendtime < #{endsendtime }


            and a.alarmstate=#{alarmstate}


            and a.direction=#{direction}


            and a.status=#{status}


            and a.signalstate=#{signalstate}


            and a.abnormaltype=#{abnormaltype}


            and a.fromflag=#{fromflag}


            and a.extendversion=#{extendversion}




    order by a.createdate desc
