mapper.select:
select

        a.id,

        a.channelid,

        a.channelname,

        a.createdate,

        a.membernum,

        a.remark,

        a.owner,

        a.status,

        a.type,

        a.deleted,

        a.updatedate
from
subiaodb.talkchannel as a

where 1=1

            and a.id=#{id}


            and a.channelid=#{channelid}



            and a.channelname=#{channelname}



            and a.createdate >= #{startcreatedate }


            and  a.createdate < #{endcreatedate }


            and a.membernum=#{membernum}


            and a.remark=#{remark}



            and a.owner=#{owner}


            and a.status=#{status}


            and a.type=#{type}

        and a.deleted=false

            and a.updatedate >= #{startupdatedate }


            and  a.updatedate < #{endupdatedate }




    order by a.updatedate desc
