mapper.select:
select

        a.vehicleid,

        a.createdate,

        a.deleted,

        a.owner,

        a.remark,

        a.buytime,

        a.depid,

        a.depname,

        a.driver,

        a.drivermobile,

        a.gpsterminaltype,

        a.industry,

        a.installdate,

        a.lastchecktime,

        a.memberid,

        a.monitor,

        a.monitormobile,

        a.motorid,

        a.offlinetime,

        a.onlinetime,

        a.operpermit,

        a.platecolor,

        a.plateno,

        a.region,

        a.routes,

        a.runstatus,

        a.simno,

        a.status,

        a.termid,

        a.usetype,

        a.vehicletype,

        a.vendor,

        a.videodeviceid,

        a.enddate,

        a.startdate,

        a.workhour,

        a.buydate,

        a.engineno,

        a.frameno,

        a.manufacture,

        a.modelno,

        a.photo,

        a.companyid,

        a.videochannelnum,

        a.username,

        a.videodatatype,

        a.channelid,

        a.videodisk,

        a.videochannelnames,

        a.flowrateno,

        a.vehiclepassword,

        a.supertranstype,

        a.seatingcapacity,

        a.transserialno,

        a.routename,

        a.servicestartdate,

        a.serviceenddate,

        a.mileageadjustment,

        a.fuelconsumption,

        a.updatedate,

        a.updatestaff,

        a.createstaff,

        a.vehiclemanufacturer,

        a.inlinedate,

        a.synchronizeddate,

        a.operatorid
from
subiaodb.vehicle as a

where 1=1

            and a.vehicleid=#{vehicleid}


            and a.createdate >= #{startcreatedate }


            and  a.createdate < #{endcreatedate }

        and a.deleted=false

            and a.owner=#{owner}



            and a.remark=#{remark}



            and a.buytime >= #{startbuytime }


            and  a.buytime < #{endbuytime }


            and a.depid=#{depid}


            and a.depname=#{depname}



            and a.driver=#{driver}



            and a.drivermobile=#{drivermobile}



            and a.gpsterminaltype=#{gpsterminaltype}



            and a.industry=#{industry}



            and a.installdate >= #{startinstalldate }


            and  a.installdate < #{endinstalldate }


            and a.lastchecktime >= #{startlastchecktime }


            and  a.lastchecktime < #{endlastchecktime }


            and a.memberid=#{memberid}


            and a.monitor=#{monitor}



            and a.monitormobile=#{monitormobile}



            and a.motorid=#{motorid}



            and a.offlinetime >= #{startofflinetime }


            and  a.offlinetime < #{endofflinetime }


            and a.onlinetime >= #{startonlinetime }


            and  a.onlinetime < #{endonlinetime }


            and a.operpermit=#{operpermit}



            and a.platecolor=#{platecolor}


            and a.plateno=#{plateno}



            and a.region=#{region}



            and a.routes=#{routes}



            and a.runstatus=#{runstatus}



            and a.simno=#{simno}



            and a.status=#{status}



            and a.termid=#{termid}


            and a.usetype=#{usetype}



            and a.vehicletype=#{vehicletype}



            and a.vendor=#{vendor}



            and a.videodeviceid=#{videodeviceid}



            and a.enddate >= #{startenddate }


            and  a.enddate < #{endenddate }


            and a.startdate >= #{startstartdate }


            and  a.startdate < #{endstartdate }


            and a.buydate >= #{startbuydate }


            and  a.buydate < #{endbuydate }


            and a.engineno=#{engineno}



            and a.frameno=#{frameno}



            and a.manufacture=#{manufacture}



            and a.modelno=#{modelno}



            and a.photo=#{photo}



            and a.companyid=#{companyid}


            and a.videochannelnum=#{videochannelnum}


            and a.username=#{username}



            and a.videodatatype=#{videodatatype}


            and a.channelid=#{channelid}


            and a.videodisk=#{videodisk}



            and a.videochannelnames=#{videochannelnames}



            and a.flowrateno=#{flowrateno}



            and a.vehiclepassword=#{vehiclepassword}



            and a.supertranstype=#{supertranstype}



            and a.seatingcapacity=#{seatingcapacity}



            and a.transserialno=#{transserialno}



            and a.routename=#{routename}



            and a.servicestartdate >= #{startservicestartdate }


            and  a.servicestartdate < #{endservicestartdate }


            and a.serviceenddate >= #{startserviceenddate }


            and  a.serviceenddate < #{endserviceenddate }


            and a.updatedate >= #{startupdatedate }


            and  a.updatedate < #{endupdatedate }


            and a.updatestaff=#{updatestaff}


            and a.createstaff=#{createstaff}


            and a.vehiclemanufacturer=#{vehiclemanufacturer}



            and a.inlinedate >= #{startinlinedate }


            and  a.inlinedate < #{endinlinedate }


            and a.synchronizeddate >= #{startsynchronizeddate }


            and  a.synchronizeddate < #{endsynchronizeddate }


            and a.operatorid=#{operatorid}




    order by a.updatedate desc
